import React, { useState } from 'react';

const Recipe = ({ index, title, description, ingredients, instructions, image }) => {
  const [isDetailsVisible, setIsDetailsVisible] = useState(false);
  console.log(title);
  // const toggleDetails = () => {
  //   setIsDetailsVisible(!isDetailsVisible);
  // };
  // const toggleDetails = () => {
  //   setIsDetailsVisible(prevShowDetails => !prevShowDetails);
  const [activeItemIndex, setActiveItemIndex] = useState(-1);

    // Function to toggle the visibility of an item's details
    // const toggleDetails = (index) => {
    //     setActiveItemIndex(activeItemIndex === index ? -1 : index);
    // }
    const showDetails = (index) => {
      setActiveItemIndex(index);
  };
  console.log(isDetailsVisible);

  return (
    <div className="recipe">
      <h2>{title}</h2>
      <img src={image} alt={`Picture of ${title}`} />
      <p className="description">{description}</p>
      {/* <button className="view-details-btn" onClick={toggleDetails}>
        View Details
      </button> */}
      {/* <button onClick={toggleDetails}>
                {setIsDetailsVisible ? 'Hide Details' : 'View Details'}
      </button> */}
      {/* <button onClick={() => toggleDetails(index)}>
        {activeItemIndex === index ? 'Hide Details' : 'View Details'}
      </button> */}
      <button onClick={() => showDetails(index)}>
        View Details
      </button>
      {activeItemIndex === index && (
        <div className='details'>
          <h3>Ingredients:</h3>
          <ul>
            {ingredients.map((ingredient, index) => (
              <li key={index}>{ingredient}</li>
            ))}
          </ul>
          <h3>Instructions:</h3>
          {instructions.map((instruction, index) => (
            <p key={index}>{instruction}</p>
          ))}
        </div>
      )}
    </div>
  );
}
export default Recipe;
